import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch} from 'react-redux';
import {register} from '../../store/actions/UserActions';
const RegisterPage = () => {
const [name, setName] = useState('');
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [image, setImage] = useState('');

const dispatch = useDispatch();
    const handleChange = (evt) => {
        if(evt.target.name == 'name') {setName(evt.target.value);}
        else if(evt.target.name == 'email') {setEmail(evt.target.value);}
        else if(evt.target.name == 'password') {setPassword(evt.target.value);}
        else if(evt.target.name == 'image') {setImage(evt.target.value);}
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();
        register({name, email, password, image});
    };
    return (
        <div>
            <form onSubmit={handleSubmit}>
    <div className="form-group">
      <label for="name">Name:</label>
      <input type="text" className="form-control" id="name" placeholder="Enter name" name="name" value={name} onChange={handleChange} />
    </div>

    <div className="form-group">
      <label for="email">email:</label>
      <input type="email" className="form-control" id="email" placeholder="Enter email" name="email" 
      value={email} onChange={handleChange} />
    </div>

    <div className="form-group">
      <label for="password">Password:</label>
      <input type="password" className="form-control" id="password" placeholder="Enter password" name="password" 
      value={password} onChange={handleChange} />
    </div>

    <div className="form-group">
      <label for="image">Image:</label>
      <input type="text" className="form-control" id="endDate" placeholder="Enter image" name="image" 
      value={image} onChange={handleChange} />
    </div>




    <button type="submit" className="btn btn-primary">Submit</button>
  </form>

        </div>
    )
}

export default RegisterPage;
