import {GET_EVENTS_REQUEST, GET_EVENTS_SUCCESS, GET_EVENTS_FAIL} from '../types/eventActionTypes';

    function authReducer ( state = {
        loading: false,
        events: [],   
      error: null
      }, action) {
        switch(action.type){
            case GET_EVENTS_REQUEST: return { ...state, loading: true};
            case GET_EVENTS_SUCCESS: return { ...state, loading: false, events: action.payload.data.events, error: null};
            case GET_EVENTS_FAIL: return { ...state, loading: false, events: [], error: action.payload };



            default: return state;
        }
      }

      export default authReducer;
